"}]},{"title":"1.3.6","codes":[{"language":"Cpp","content":"class Solution {
public:
  vector<vector<int>> ans;
  void dfs(int idx, vector<int> path, vector<int> nums, int n,
           vector<bool> visited) {
    // 
    // 1. 
    if (path.size() == n) {
      vector<int> temp(path.size());
      std::copy(path.begin(), path.end(), temp.begin());
      ans.push_back(temp);
      return;
    }

    // 2. 
    if (idx == n)
      return;

    // 
    for (int i = 0; i < n; ++i) {
      int num = nums[i];
      // 
      if (visited[i])
        continue;
      // 
      visited[i] = true;
      path.push_back(num);
      dfs(idx + 1, path, nums, n, visited);
      // 
      path.pop_back();
      visited[i] = false;
    }
  }
  vector<vector<int>> permute(vector<int> &nums) {
    vector<int> path;
    vector<bool> visited(nums.size(), false);
    int n = nums.size();
    dfs(0, path, nums, n, visited);
    return ans;
  }
};

"},{"language":"Java","content":"class Solution {
  private void backtrack(
      List<List<Integer>> ans, ArrayList<Integer> tempList, ArrayList<Integer> nums) {
    if (tempList.size() == nums.size()) {
      ans.add(new ArrayList<Integer>(tempList));
      return;
    }
    for (int i = 0; i < nums.size(); i++) {
      if (tempList.contains(nums.get(i))) continue;
      tempList.add(nums.get(i));
      backtrack(ans, tempList, nums);
      tempList.remove(tempList.size() - 1);
    }
  }

  public List<List<Integer>> permute(int[] nums) {
    List<List<Integer>> ans = new LinkedList();
    ArrayList<Integer> tempList = new ArrayList<Integer>();
    ArrayList<Integer> anums = new ArrayList<Integer>();
    for (int num : nums) anums.add(num);
    backtrack(ans, tempList, anums);
    return ans;
  }
}

"},{"language":"Js","content":"function backtrack(list, tempList, nums) {
  if (tempList.length === nums.length) return list.push([...tempList])
  for (let i = 0; i < nums.length; i++) {
    if (tempList.includes(nums[i])) continue
    tempList.push(nums[i])
    backtrack(list, tempList, nums)
    tempList.pop()
  }
}
/**
 * @param {number[]} nums
 * @return {number[][]}
 */
const permute = function(nums) {
  const list = []
  backtrack(list, [], nums)
  return list
}

"},{"language":"Py","content":"class Solution:
    def permute(self, nums: List[int]) -> List[List[int]]:
        def dfs(idx: int, path: List[int]) -> None:
            # 
            # 1. 
            if len(path) == n:
                ans.append(path.copy())
                return
            # 2. 
            if idx == n:
                return

            # 
            for num in nums:
                # 
                if num in visited:
                    continue
                # 
                visited.add(num)
                path.append(num)
                dfs(idx + 1, path)
                # 
                path.pop()
                visited.remove(num)

        ans = []
        visited = set()
        n = len(nums)
        dfs(0, [])
        return ans

"}]}]},{"id":"02","name":"","list":[{"title":"2.1.1","codes":[{"language":"Cpp","content":"class Solution {
