class Solution {
public:
  vector<vector<int>> res;
  set<string> set;
  void backtrack(vector<int> &nums, int n, vector<int> tempList, int remain,
                 int start) {
    if (tempList.size() > 4) {
      return;
    }
    if (remain == 0 && tempList.size() == 4) {
      stringstream ss;
      copy(tempList.begin(), tempList.end(), ostream_iterator<int>(ss, ""));
      string s = ss.str();
      if (set.count(s) != 0) {
        return;
      } else {
        set.insert(s);
        vector<int> copy;
        copy.assign(tempList.begin(), tempList.end());
        res.push_back(copy);
        return;
      }
    }
    for (int i = start; i < n; i++) {
      tempList.push_back(nums[i]);
      backtrack(nums, n, tempList, remain - nums[i], i + 1);
      tempList.pop_back();
    }
  }
  vector<vector<int>> fourSum(vector<int> &nums, int target) {
    sort(nums.begin(), nums.end());
    vector<int> tempList;
    backtrack(nums, nums.size(), tempList, target, 0);
    return res;
  }
};
"},{"language":"Java","content":"class Solution {
  private void backtrack(
      List<List<Integer>> res,
      int[] nums,
      int n,
      List<Integer> tempList,
      int remain,
      int start,
      HashSet set) {
    if (tempList.size() > 4) {
      return;
    }
    if (remain == 0 && tempList.size() == 4) {
      if (set.contains(tempList.toString())) {
        return;
      } else {
        set.add(tempList.toString());
        res.add(new ArrayList<Integer>(tempList));
        return;
      }
    }
    for (int i = start; i < n; i++) {
      tempList.add(nums[i]);
      backtrack(res, nums, n, tempList, remain - nums[i], i + 1, set);
      tempList.remove(tempList.size() - 1);
    }
  }

  public List<List<Integer>> fourSum(int[] nums, int target) {
    List<List<Integer>> res = new ArrayList<>();
    HashSet set = new HashSet();
    Arrays.sort(nums);
    List<Integer> tempList = new ArrayList<Integer>();
    backtrack(res, nums, nums.length, tempList, target, 0, set);
    return res;
  }
}

"},{"language":"Py","content":"class Solution:
    def backtrack(
        self,
        res: List[List[int]],
        nums: List[int],
        n: int,
        tempList: List[int],
        remain: int,
        start: int,
        hashmap: dict,
    ) -> None:
        if len(tempList) > 4:
            return
        if remain == 0 and len(tempList) == 4:
            if str(tempList) in hashmap:
                return
            else:
                hashmap[str(tempList)] = True
                res.append(tempList.copy())
                return
        for i in range(start, n):
            tempList.append(nums[i])
            self.backtrack(res, nums, n, tempList, remain - nums[i], i + 1, hashmap)
            tempList.pop()

    def fourSum(self, nums: List[int], target: int) -> List[List[int]]:
        res = []
        hashmap = {}
        nums.sort()
        self.backtrack(res, nums, len(nums), [], target, 0, hashmap)
        return res

"}]},{"title":"2.3.2","codes":[{"language":"Cpp","content":"#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
  vector<vector<int> > fourSum(vector<int>& nums,
      int target) {
    sort(nums.begin(), nums.end());
    vector<vector<int> > results;
    vector<int> tempList;
    findNsum(nums, target, 4, tempList, results);
    return results;
  }
  void findNsum(vector<int> nums, int target, int N,
      vector<int> tempList,
      vector<vector<int> >& results) {
    if (nums.size() < N || N < 2) return;
    if (N == 2) {
      int l = 0, r = nums.size() - 1;
      while (l < r) {
        if (nums[l] + nums[r] == target) {
          vector<int> bufList = tempList;
          bufList.push_back(nums[l]);
          bufList.push_back(nums[r]);
          results.push_back(bufList);
          l += 1;
          r -= 1;
          while (l < r && nums[l] == nums[l - 1])
            l += 1;
          while (r > l && nums[r] == nums[r + 1])
            r -= 1;
        }
        else if (nums[l] + nums[r] < target) l += 1;
        else r -= 1;
      }
    } else {
      for (int i = 0; i < nums.size(); i++)
        if (i == 0 || i > 0 && nums[i - 1] != nums[i]) {
          vector<int> buf_nums;
          for (int j = i + 1; j < nums.size(); j++)
            buf_nums.push_back(nums[j]);
          vector<int> bufList = tempList;
          bufList.push_back(nums[i]);
          findNsum(buf_nums, target - nums[i],
              N - 1, bufList, results);
        }
    }
  }
};

"},{"language":"Java","content":"import java.util.*;
import java.lang.Math;

public class Solution {
  public List<List<Integer> > fourSum(
      int[] nums, int target
      ) {
    Arrays.sort(nums);
    List<List<Integer> > results = new ArrayList<List<Integer> >();
    List<Integer> tempList = new ArrayList<>();
    findNsum(nums, target, 4, tempList, results);
    return results;
  }
  void findNsum(int[] nums, int target, int N,
      List<Integer> tempList,
      List<List<Integer> > results) {
    if (nums.length < N || N < 2) return;
    if (N == 2) {
