      total += nums[i];
      right_prefix_max_sum = Math.max(right_prefix_max_sum,
          total);
    }
    int cross_max_sum = left_suffix_max_sum
      + right_prefix_max_sum
      + nums[mid];
    return Math.max(cross_max_sum, Math.max(left, right));
