            if i > 0 and nums[i] == nums[i - 1] and not visited[i - 1]:
                continue
            visited[i] = True
            tmp.append(nums[i])
            self.backtracking(nums, tmp, visited, perm)
            visited[i] = False
            tmp.pop()

