private:
    int get_neighbor_count(int i, int j, vector<vector<int>>& board) {
        int top = std::max(0, i - 1);
        int bottom = std::min<int>(board.size() - 1, i + 1);
        int left = std::max(0, j - 1);
        int right = std::min<int>(board[0].size() - 1, j + 1);

        int count = 0;
        for (int x = top; x < bottom + 1; x++)
            for (int y = left; y < right + 1; y++)
                if (board[x][y] == 1 || board[x][y] == -1)
                    count += 1;

        return count;
    }
public:
    void gameOfLife(vector<vector<int>>& board) {
        for (int i = 0; i < board.size(); i++) {
            for (int j = 0; j < board[0].size(); j++) {
                int res = get_neighbor_count(i, j, board);
                if (board[i][j] == 1 && (res == 3 || res == 4))
                    board[i][j] = 1;
                else if (board[i][j] == 1)
                    board[i][j] = -1;
                else if (board[i][j] == 0 && res == 3)
                    board[i][j] = -2;
            }
        }

        for (int i = 0; i < board.size(); i++) {
            for (int j = 0; j < board[0].size(); j++) {
                if (board[i][j] == -2)
                    board[i][j] = 1;
                else if (board[i][j] == -1)
                    board[i][j] = 0;
            }
        }
    }
};

"},{"language":"Java","content":"public class GameOfLife {
    public void gameOfLife(int[][] board) {
        // do in place life check
        for (int i = 0; i < board.length; i++) {
            for (int j = 0; j < board[i].length; j++) {
                board[i][j] = checkLifeInPlace(board, board[i][j], i, j);
            }
        }
        // translate
        for (int i = 0; i < board.length; i++) {
            for (int j = 0; j < board[i].length; j++) {
                if (board[i][j] == 2)
                    board[i][j] = 1;
                else if (board[i][j] == -1)
                    board[i][j] = 0;
            }
        }
    }

    private int checkLifeInPlace(int[][] board, int preState, int i, int j) {
        int neighborCount = getNeighborCountForInPlace(board, preState, i, j);
        if (neighborCount < 2 || neighborCount > 3)
            return preState == 0 ? 0 : -1;
        else if (neighborCount == 3)
            return preState == 1 ? 1 : 2;
        else
            return preState;
    }

    //      -1 as for 1 to 0;
    //      2 as for 0 to 1;
    private int getNeighborCountForInPlace(int[][] board, int preState, int i, int j) {
        int count = 0;
        int maxRow = board.length - 1, maxColumn = board[0].length - 1;

        if (i != 0) {
            count += board[i - 1][j] == 1 || board[i - 1][j] == -1 ? 1 : 0;
            if (j != 0) {
                count += board[i - 1][j - 1] == 1 || board[i - 1][j - 1] == -1 ? 1 : 0;
            }
            if (j != maxColumn) {
                count += board[i - 1][j + 1] == 1 || board[i - 1][j + 1] == -1 ? 1 : 0;
            }

        }

        if (i != maxRow) {
            count += board[i + 1][j] == 1 || board[i + 1][j] == -1 ? 1 : 0;
            if (j != 0) {
                count += board[i + 1][j - 1] == 1 || board[i + 1][j - 1] == -1 ? 1 : 0;
            }
            if (j != maxColumn) {
                count += board[i + 1][j + 1] == 1 || board[i + 1][j + 1] == -1 ? 1 : 0;
            }
        }

        if (j != 0) {
            count += board[i][j - 1] == 1 || board[i][j - 1] == -1 ? 1 : 0;
        }
        if (j != maxColumn) {
            count += board[i][j + 1] == 1 || board[i][j + 1] == -1 ? 1 : 0;
        }
        return count;
    }
}
