      return links[ch -'a'] != null;
  }
  public TrieNode get(char ch) {
      return links[ch -'a'];
  }
  public void put(char ch, TrieNode node) {
      links[ch -'a'] = node;
  }
  public void setEnd() {
      isEnd = true;
  }
  public boolean isEnd() {
      return isEnd;
  }
