  Map<Integer, Node> cache; // 
  Map<Integer, DoublyLinkedList> freqMap; // 
  int size;
  int capacity;
  int min; // 

  public LFUCache(int capacity) {
      cache = new HashMap<> (capacity);
      freqMap = new HashMap<>();
      this.capacity = capacity;
  }

  public int get(int key) {
      Node node = cache.get(key);
      if (node == null) {
          return -1;
      }
      freqInc(node);
      return node.value;
  }

  public void put(int key, int value) {
      if (capacity == 0) {
          return;
      }
      Node node = cache.get(key);
      if (node != null) {
          node.value = value;
          freqInc(node);
      } else {
          if (size == capacity) {
              DoublyLinkedList minFreqLinkedList = freqMap.get(min);
              cache.remove(minFreqLinkedList.tail.pre.key);
              minFreqLinkedList.removeNode(minFreqLinkedList.tail.pre); // min, addnewNodemin1.
              size--;
          }
          Node newNode = new Node(key, value);
          cache.put(key, newNode);
          DoublyLinkedList linkedList = freqMap.get(1);
          if (linkedList == null) {
              linkedList = new DoublyLinkedList();
              freqMap.put(1, linkedList);
          }
          linkedList.addNode(newNode);
          size++;  
          min = 1;   
      }
  }

  void freqInc(Node node) {
      // freq, min
      int freq = node.freq;
      DoublyLinkedList linkedList = freqMap.get(freq);
      linkedList.removeNode(node);
      if (freq == min && linkedList.head.post == linkedList.tail) { 
          min = freq + 1;
      }
      // freq
      node.freq++;
      linkedList = freqMap.get(freq + 1);
      if (linkedList == null) {
          linkedList = new DoublyLinkedList();
          freqMap.put(freq + 1, linkedList);
      }
      linkedList.addNode(node);
  }
}

class Node {
  int key;
  int value;
  int freq = 1;
  Node pre;
  Node post;

  public Node() {}

  public Node(int key, int value) {
      this.key = key;
      this.value = value;
  }
}

class DoublyLinkedList {
  Node head;
  Node tail;

  public DoublyLinkedList() {
      head = new Node();
      tail = new Node();
      head.post = tail;
      tail.pre = head;
  }

  void removeNode(Node node) {
      node.pre.post = node.post;
      node.post.pre = node.pre;
  }

  void addNode(Node node) {
      node.post = head.post;
      head.post.pre = node;
      head.post = node;
      node.pre = head;
  }
}
