class SkipNode{
public:
    int val;
    int level;
    SkipNode** next;
    SkipNode(int v,int l){
        val=v;
        level=l;
        next=new SkipNode*[l+1]();
    }
};

class Skiplist {
private:

public:
    SkipNode* head;
    int Listlevel;
    Skiplist() {
        head=new SkipNode(INT_MIN,MAX_LEVEL);
        Listlevel=0;
    }
    int getlevel(){
        int lev=0;
        while(lev<MAX_LEVEL && lev<=Listlevel && rand()<RAND_MAX/2){
            ++lev;                      //lev1/2^lev
        }
        if(Listlevel<lev)++Listlevel;   //
        return lev;
    }
    bool search(int target) {
        SkipNode* sn=head;
        int ll=Listlevel;
        while(ll>=0){
            while(sn->next[ll]!=nullptr && sn->next[ll]->val<=target)sn=sn->next[ll];
            --ll;
        }
        if(sn->val==target)return true;
        return false;
    }

    void add(int num) {
        //if(search(num))return; //
        SkipNode* sn=head;
        int lev=getlevel();
        int ll=Listlevel;
        SkipNode* nsn=new SkipNode(num,lev);
        while(ll>=0){
            while(sn->next[ll]!=nullptr && sn->next[ll]->val<num)sn=sn->next[ll];
            if(ll<=lev){
                nsn->next[ll]=sn->next[ll];
                sn->next[ll]=nsn;
            }
            --ll;
        }
    }

    bool erase(int num) {
        if(!search(num))return false;
        SkipNode* sn=head;
        SkipNode* dsn;
        int ll=Listlevel;
        while(ll>=0){
            while(sn->next[ll]!=nullptr && sn->next[ll]->val<num)sn=sn->next[ll];
            if(ll==0)dsn=sn->next[ll];
            if(sn->next[ll]!=nullptr && sn->next[ll]->val==num){
                sn->next[ll]=sn->next[ll]->next[ll];
            }
            --ll;
        }
        if(dsn->level==Listlevel){              //
                                                //,
                                                //
            if(head->next[Listlevel]==nullptr)--Listlevel;
        }
        return true;
    }
};
