    public int[] maxSlidingWindow(int[] nums, int k) {
        if (k == 1)
            return nums;
        int turn = nums.length - k + 1;
        int[] res = new int[turn];
        for (int i = 0; i < turn; i++) {
            res[i] = getMax(nums, i, k);
        }
        return res;
