        int left = 0, right = 0;
        Map<Character, Integer> tMap = new HashMap<>();
        for (char c : t.toCharArray()) {
            tMap.put(c, tMap.getOrDefault(c, 0) + 1);
        }

        int required = tMap.keySet().size(), formed = 0;
        Map<Character, Integer> windowMap = new HashMap<>();

        // ans[0] ans[1]ans[2]
