    if (n <= 0) return;
    vector<vector<int> > old = board;
    for (int i = 0; i < m; i++)
      for (int j = 0; j < n; j++) {
        int cnt = cntLiveCell(i, j, old);
        if (old[i][j] == 0 && cnt == 3)
          board[i][j] = 1;
        if (old[i][j] == 1 && (cnt > 3 || cnt < 2))
          board[i][j] = 0;
      }
  }
};

