      for (int j = 0; j < n; j++) {
        int cnt = cntLiveCell(i, j, board);
        board[i][j] |= cnt << 1;
      }
    for (int i = 0; i < m; i++)
      for (int j = 0; j < n; j++) {
        int cell = board[i][j] & 1;
        int cnt = board[i][j] >> 1;
        if (cell == 0 && cnt == 3) board[i][j] = 1;
        else if (cell == 1 && (cnt > 3 || cnt < 2))
          board[i][j] = 0;
        else board[i][j] = cell;
      }
  }
